import threading

def initer(func):
    t = threading.currentThread()
    while getattr(t, "do_run", True):
        func()

def getThreads():
    return threading.enumerate()

def getThreadByName(name):
    for thread in getThreads():
        if thread.name == name:
            return thread

def createThread(name, func):
    threading.Thread(target=initer, args=(func,), name=name).start()

def stopThread(name):
    t = getThreadByName(name)
    t.do_run = False
    t.join()